import "core-js/modules/es.array.push.js";
import Vue from 'vue';
export default {
  name: "orders"
};
new Vue({
  data() {
    return {
      items: [{
        name: '',
        quantity: 0,
        amount: 0,
        total: 0
      }]
    };
  },
  watch: {
    'items': {
      handler(newValue, oldValue) {
        newValue.forEach(item => {
          item.total = item.quantity * item.amount;
        });
      },
      deep: true
    }
  },
  methods: {
    AddItem() {
      this.items.push({
        name: '',
        quantity: 0,
        amount: 0,
        total: 0
      });
    },
    removeItem() {
      this.items.splice(this.items, 1);
    }
  }
}).$mount("#app");
